import React, { useState, useEffect } from 'react';
import 'react-modern-calendar-datepicker/lib/DatePicker.css';
import { Calendar } from 'react-modern-calendar-datepicker';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

function ReserveTool () {
    const { id } = useParams();
    const navigate = useNavigate();
	const [selectedDayRange, setSelectedDayRange] = useState({
		from: null,
		to: null,
	});

    function getToolInfo(id) {
            const url = `https://cryptic-dusk-16798.herokuapp.com/listing/${id}`;
            // const url = `http://localhost:8000/listing/${id}`;
            console.log(url)
            fetch(url)
                .then((res) => res.json())
                .then((res) => {
                    console.log(res);
                    console.log(selectedDayRange)
                    // setSelectedDayRange(res);
                })
                .catch(console.error);
        }
    function handleSubmit(event) {
            event.preventDefault();
            console.log(selectedDayRange);
            // axios
            //     .put(
            //         `https://cryptic-dusk-16798.herokuapp.com/listing/${id}`,
            //         selectedDayRange
            //     )
            //     .then((res) => {
            //         console.log(res);
            //         navigate('/');
            //     });

            // console.log(selectedDayRange);
        }

    useEffect(() => {
        getToolInfo(id);
    }, []);

    if (!selectedDayRange) {
			return <p>Loading tool information ...</p>;
		}

	return (
        <form onSubmit={handleSubmit}>

            <Calendar
                value={selectedDayRange}
                onChange={setSelectedDayRange}
                />
        </form>
	);
};

export default ReserveTool;

