import React, { useState, useEffect } from 'react';
import 'react-modern-calendar-datepicker/lib/DatePicker.css';
import DatePicker, { Calendar } from 'react-modern-calendar-datepicker';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

function ReserveTool () {
    const { id } = useParams();
    const navigate = useNavigate();
	const [selectedDayRange, setSelectedDayRange] = useState([]);
    const [tool, setTool]= useState({
        tool: '',
        brand: '',
        model: '',
        category: '',
        condition: '',
        cordless: Boolean,
        available: Boolean,
        location: '',
        price: Number,
        description: '',
        reserve: []
    })

    function getToolInfo(id) {
            // const url = `https://cryptic-dusk-16798.herokuapp.com/listing/${id}`;
            const url = `http://localhost:8000/listing/${id}`;
            console.log(url)
            fetch(url)
                .then((res) => res.json())
                .then((res) => {
                    console.log(res);
                    console.log(selectedDayRange)
                    setTool(res);
                })
                .catch(console.error);
        console.log(tool)
        }
    function handleSubmit(event) {
            event.preventDefault();
            console.log(selectedDayRange);
            console.log(tool.reserve)
            tool.reserve.push(selectedDayRange)

            // axios
            //     .put(
            //         `https://cryptic-dusk-16798.herokuapp.com/listing/${id}`,
            //         selectedDayRange
            //     )
            //     .then((res) => {
            //         console.log(res);
            //         navigate('/');
            //     });

            // console.log(selectedDayRange);
        }
        // function displayDates(){
        //     console.log('hi')
        //     setSelectedDayRange()
        // }

    useEffect(() => {
        getToolInfo(id);
    }, []);

    if (!selectedDayRange) {
			return <p>Loading tool information ...</p>;
		}

	return (
        <form >

            <Calendar
                value={selectedDayRange}
                onChange={setSelectedDayRange}
                />
            <button onClick={handleSubmit}>Submit</button>
        </form>
	);
};

export default ReserveTool;

