import React, { useState, useEffect } from 'react';
import 'react-modern-calendar-datepicker/lib/DatePicker.css';
import { Calendar } from 'react-modern-calendar-datepicker';
import { useParams } from 'react-router-dom';

function ReserveTool () {
    const { id } = useParams();
	const [selectedDayRange, setSelectedDayRange] = useState({
		from: null,
		to: null,
	});

    function getToolInfo(id) {
            const url = `https://cryptic-dusk-16798.herokuapp.com/listing/${id}`;
            fetch(url)
                .then((res) => res.json())
                .then((res) => {
                    console.log(res);
                    setSelectedDayRange(res);
                })
                .catch(console.error);
        }
    function handleSubmit(event) {
            event.preventDefault();
            console.log(formData);
            axios
                .put(
                    `https://cryptic-dusk-16798.herokuapp.com/listing/${id}`,
                    formData
                )
                .then((res) => {
                    console.log(res);
                    navigate('/');
                });

            console.log(formData);
        }

    useEffect(() => {
        getToolInfo(id);
    }, []);

	return (
        <form onSubmit={handleSubmit}>

            <Calendar
                value={selectedDayRange}
                onChange={setSelectedDayRange}
                />
        </form>
	);
};

export default ReserveTool;

